import React, { useState } from "react";
import Navbar from "./components/navbar/Navbar";
import DatePicker from "./components/datepicker/DatePicker";
import Input from "./components/input/Input";
import "./App.css";
import "./Contacto.css";

export default function Contacto() {
  const [scheduled, setScheduled] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [dpSelection, setDpSelection] = useState({ date: null, time: null });
  const [nameError, setNameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [dateError, setDateError] = useState("");
  const [timeError, setTimeError] = useState("");

  function handleConfirm({ date, time }) {
    // validate parent fields before scheduling
    let ok = true;
    if (!name) {
      setNameError("Por favor completá el nombre.");
      ok = false;
    }
    if (!email || !(email.includes("@") && email.includes("."))) {
      setEmailError(!email ? "Por favor ingresá tu email." : "Email inválido.");
      ok = false;
    }
    if (!date) {
      setDateError("Elegí un día.");
      ok = false;
    }
    if (!time) {
      setTimeError("Elegí un horario.");
      ok = false;
    }

    if (!ok) return;

    // all good
    setNameError("");
    setEmailError("");
    setDateError("");
    setTimeError("");
    setScheduled({ date, time });
  }

  return (
    <div className="contact-page">
      <header>
        <Navbar />
      </header>

      <main className="contact-main d-flex justify-content-space-between align-items-center">
        <div>
          <h1>¿ Querés crecer ?</h1>
          <h2>Hablemos !</h2>
          <h3>Agendá un encuentro y veamos cómo llevar tu negocio para arriba</h3>
          <p>
            Elegí un día y un horario disponible para que coordinemos una
            llamada.
          </p>
        </div>

        <div className="contact-form">
          <Input label="Nombre" name="name" value={name} onChange={(e) => setName(e.target.value)} />

          <Input
            label="Mensaje"
            name="message"
            textarea
            rows={6}
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />

          <div>
            <DatePicker
              onConfirm={handleConfirm}
              onChange={(sel) => setDpSelection(sel)}
              externalConfirm={true}
              start="09:00"
              end="17:00"
              step={30}
              minDays={1}
            />
          </div>

          <Input label="Email" name="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} />

          <div style={{ marginTop: 12 }}>
            <button
              type="button"
              className="Button boton-primario"
              disabled={
                !name || !message || !email || !dpSelection.date || !dpSelection.time || !(email.includes("@") && email.includes("."))
              }
              onClick={() => {
                console.log("Agendar clicked", { name, email, message, dpSelection });
                if (!name || !message || !email || !dpSelection.date || !dpSelection.time) return;
                setScheduled({ date: dpSelection.date, time: dpSelection.time });
              }}
            >
              Agendar reunión
            </button>

            <button style={{ marginLeft: 8 }} className="Button boton-secundario" onClick={() => (window.location.hash = "")}>Volver</button>
          </div>

          {/* Mostrar errores de validación simples */}
          {(!name || !message || !email || !dpSelection.date || !dpSelection.time || !(email.includes("@") && email.includes("."))) && (
            <div style={{ color: "#b33", marginTop: 10 }}>
              <div>{!name && "Por favor completá el nombre."}</div>
              <div>{!message && "Por favor escribí un mensaje."}</div>
              <div>{!email && "Por favor ingresá tu email."}{email && !(email.includes("@") && email.includes(".")) && " Email inválido."}</div>
              <div>{!dpSelection.date && "Elegí un día."}{dpSelection.date && !dpSelection.time && " Elegí un horario."}</div>
            </div>
          )}

          {scheduled && (
            <div style={{ marginTop: 16, padding: 12, background: "#f6f7ff", borderRadius: 8 }}>
              <strong>Reunión agendada:</strong>
              <div>{scheduled.date} a las {scheduled.time}</div>
              <div style={{ marginTop: 8 }}>Revisá tu casilla, te enviaremos un correo de confirmación.</div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}
