import React, { useState } from "react";
import DatePicker from "./components/datepicker/DatePicker";
import "./App.css";
import "./Contacto.css";

export default function Contacto() {
  const [scheduled, setScheduled] = useState(null);

  function handleConfirm({ date, time }) {
    setScheduled({ date, time });
  }

  return (
    <div className="contact-page">
      <main className="contact-main">
        <div className="contact-hero">
          <h1>¿ Querés crecer ?</h1>
          <h2>Hablemos !</h2>
          <h3>Agendá un encuentro y veamos cómo llevar tu negocio para arriba</h3>
          <p>Elegí un día y un horario disponible para que coordinemos una llamada.</p>
        </div>

        <div className="contact-content">
          <form className="contact-form">
            <label>
              Nombre
              <input className="contact-input" type="text" name="name" />
            </label>

            <label style={{ marginTop: 12 }}>
              Mensaje
              <textarea className="contact-textarea" name="message" rows={6} />
            </label>

            <div className="contact-actions">
              <button type="button" className="Button boton-primario" onClick={() => alert("Enviar (mock)")}>Enviar</button>
              <button type="button" className="Button boton-secundario" style={{ marginLeft: 8 }} onClick={() => (window.location.hash = "")}>
                Volver
              </button>
            </div>
          </form>

          <div className="contact-scheduler">
            <DatePicker onConfirm={handleConfirm} start="09:00" end="17:00" step={30} minDays={1} />

            {scheduled && (
              <div className="scheduled-box">
                <strong>Reunión agendada:</strong>
                <div>{scheduled.date} a las {scheduled.time}</div>
                <div className="scheduled-note">Te enviaremos un correo de confirmación (mock).</div>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}
