import React, { useState } from "react";
import Navbar from "./components/navbar/Navbar";
import DatePicker from "./components/datepicker/DatePicker";
import "./App.css";

export default function Contacto() {
  const [scheduled, setScheduled] = useState(null);

  function handleConfirm({ date, time }) {
    setScheduled({ date, time });
  }

  return (
    <div className="contact-page">
      <header>
        <Navbar />
      </header>
      <main className="contact-main">
        <h2>¿ Querés crecer ? <br/>Hablemos !</h2>
        <p>Agendá un encuentro y veamos cómo llevar tu negocio para arriba</p>

        <div style={{ maxWidth: 640, margin: "24px auto", textAlign: "left" }}>
          <label>
            Nombre
            <input type="text" name="name" style={{ width: "100%", padding: "8px", marginTop: 8 }} />
          </label>
          <label style={{ display: "block", marginTop: 12 }}>
            Mensaje
            <textarea name="message" rows="6" style={{ width: "100%", padding: 8, marginTop: 8 }} />
          </label>
          <div style={{ marginTop: 12 }}>
            <button className="Button boton-primario" onClick={() => alert("Enviar (mock)")}>Enviar</button>
            <button style={{ marginLeft: 8 }} className="Button boton-secundario" onClick={() => (window.location.hash = "")}>Volver</button>
          </div>

          <hr style={{ margin: "24px 0" }} />

          <h3>Agendá una reunión</h3>
          <p>Elegí un día y un horario disponible para que coordinemos una llamada.</p>

          <div style={{ marginTop: 12 }}>
            <DatePicker onConfirm={handleConfirm} start="09:00" end="17:00" step={30} minDays={1} />
          </div>

          {scheduled && (
            <div style={{ marginTop: 16, padding: 12, background: "#f6f7ff", borderRadius: 8 }}>
              <strong>Reunión agendada:</strong>
              <div>{scheduled.date} a las {scheduled.time}</div>
              <div style={{ marginTop: 8 }}>
                Te enviaremos un correo de confirmación (mock).
              </div>
            </div>
          )}
        </div>
      </main>
      
    </div>
  );
}
