import React, { useState, useEffect } from "react";
import "./Card.css";

const PLACEHOLDER = "https://via.placeholder.com/300x200?text=No+image";

function resolveImageSrc(img) {
  // Accepts: string URL, imported module, array of images, or object with src/url/default
  if (!img) return PLACEHOLDER;
  if (Array.isArray(img)) return resolveImageSrc(img[0]);
  if (typeof img === "string") return img;
  if (typeof img === "object") {
    if (img.src) return img.src;
    if (img.url) return img.url;
    if (img.default) return img.default;
  }
  return PLACEHOLDER;
}

export default function Card({ image, title, text }) {
  const [src, setSrc] = useState(() => resolveImageSrc(image));

  useEffect(() => {
    setSrc(resolveImageSrc(image));
  }, [image]);

  function handleError() {
    setSrc(PLACEHOLDER);
  }

  return (
    <div className="card">
      <img
        src={src}
        alt={title || "Card image"}
        className="card-img"
        loading="lazy"
        onError={handleError}
      />

      <div className="card-content">
        <h3 className="card-title">{title}</h3>
        <p className="card-text">{text}</p>
      </div>
    </div>
  );
}
