import React from 'react';
import Card from './Card';
import './Card.css';

export default function CardsGrid({ items = [] }) {
  return (
    <div className="cards-container">
      {items.map((it, idx) => (
        <Card
          key={idx}
          image={it.image}
          title={it.title}
          text={it.text}
        />
      ))}
    </div>
  );
}
