import React, { useState } from "react";
import Navbar from "./components/navbar/Navbar";
import DatePicker from "./components/datepicker/DatePicker";
import Input from "./components/input/Input";
import "./App.css";
import "./Contacto.css";

export default function Contacto() {
  const [scheduled, setScheduled] = useState(null);
  const [name, setName] = useState("");
  const [message, setMessage] = useState("");
  const [email, setEmail] = useState("");
  const [dpSelection, setDpSelection] = useState({ date: null, time: null });

  function handleConfirm({ date, time }) {
    setScheduled({ date, time });
  }

  return (
    <div className="contact-page">
      <header>
        <Navbar />
      </header>
      <main className="contact-main d-flex justify-content-space-between align-items-center">
        <div>
          <h1>¿ Querés crecer ?</h1>
          <h2>Hablemos !</h2>
          <h3>
            Agendá un encuentro y veamos cómo llevar tu negocio para arriba
          </h3>
          <p>
            Elegí un día y un horario disponible para que coordinemos una
            llamada.
          </p>
        </div>
        <div className="contact-form">
          <Input
            label="Nombre"
            name="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Input
            label="Email"
            name="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <div>
            <DatePicker
              onConfirm={handleConfirm}
              onChange={(sel) => setDpSelection(sel)}
              externalConfirm={true}
              start="09:00"
              end="17:00"
              step={30}
              minDays={1}
            />
          </div>

          <div style={{ marginTop: 12 }}>
            <button
              className="Button boton-primario"
              disabled={
                !name ||
                !message ||
                !email ||
                !dpSelection.date ||
                !dpSelection.time ||
                !(email.includes("@") && email.includes("."))
              }
              onClick={() => {
                // final validation and set scheduled
                if (
                  !name ||
                  !message ||
                  !email ||
                  !dpSelection.date ||
                  !dpSelection.time
                )
                  return;
                setScheduled({
                  date: dpSelection.date,
                  time: dpSelection.time,
                });
              }}
            >
              Agendar reunión
            </button>
            <button
              style={{ marginLeft: 8 }}
              className="Button boton-secundario"
              onClick={() => (window.location.hash = "")}
            >
              Volver
            </button>
          </div>

          {scheduled && (
            <div
              style={{
                marginTop: 16,
                padding: 12,
                background: "#f6f7ff",
                borderRadius: 8,
              }}
            >
              <strong>Reunión agendada:</strong>
              <div>
                {scheduled.date} a las {scheduled.time}
              </div>
              <div style={{ marginTop: 8 }}>
                Revisá tu casilla, te enviaremos un correo de confirmación.
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}
